.bookNavigation{display:table;border:solid 2px #0A3B93;width:100%}.bookNavigation p{display:table-row}.bookNavigation .prev{display:table-cell;vertical-align:top;text-align:left;padding:5px 0 5px 20px}.bookNavigation .up{display:table-cell;vertical-align:top;text-align:center;padding:5px 0}.bookNavigation .next{display:table-cell;vertical-align:top;text-align:right;padding:5px 20px 5px 0}